scripts.afterResponse:
const code = insomnia.response.code;
if (code >= 200 && code < 300) {
  try {
    const body = insomnia.response.json();
    const token = body?.access_token || body?.accessToken || body?.token;
    if (token) {
      insomnia.environment.set('access_token', token);
      console.log('✅ Access token saved:', token.substring(0, 20) + '...');
    }

    // Extract company_id from user object
    const companyId = body?.user?.companyId || body?.companyId;
    if (companyId) {
      insomnia.environment.set('company_id', companyId);
      console.log('✅ Company ID saved:', companyId);
    }

    // Extract user_id from user object
    const userId = body?.user?.id || body?.userId;
    if (userId) {
      insomnia.environment.set('user_id', userId);
      console.log('✅ User ID saved:', userId);
    }

    if (!token) {
      console.log('❌ No token found in response:', JSON.stringify(body));
    }
  } catch (e) {
    console.log('❌ Error parsing response:', e.message);
  }
} else {
  console.log('❌ Login failed with status:', code);
}

