scripts.afterResponse:
const code = insomnia.response.code;
if (code >= 200 && code < 300) {
  const body = insomnia.response.json();

  // Try common shapes:
  // 1) { accessToken: "..." }
  // 2) { access_token: "..." }
  // 3) { data: { accessToken: "..." } }
  // 4) { data: { access_token: "..." } }
  const token =
    body?.accessToken ??
    body?.access_token ??
    body?.data?.accessToken ??
    body?.data?.access_token;

  if (token) {
    insomnia.environment.set("access_token", token);
    console.log("✅ access_token saved to environment");
  } else {
    console.log("⚠️ Token not found in response:", body);
  }

  // Optional helpers (only set if present)
  const companyId = body?.companyId ?? body?.company_id ?? body?.data?.companyId ?? body?.data?.company_id;
  const userId = body?.userId ?? body?.user_id ?? body?.data?.userId ?? body?.data?.user_id;

  if (companyId) insomnia.environment.set("company_id", companyId);
  if (userId) insomnia.environment.set("user_id", userId);
} else {
  console.log("❌ Login failed:", code, insomnia.response.text());
}
